import React, { useReducer } from 'react'
import { Button } from '../../components/button'
import styled from 'styled-components';
import tw from 'twin.macro';
const ButtonsContainer = styled.div`
  ${tw`
    flex
    flex-wrap
    mt-4
  `};
`;

const initial = {
    count: 5
}

const reducerMeth = (state,action)=>{
    switch(action.type){
        case "Increment":
            return {...state, count: state.count + 1}
            break
         case "Decrement":
            return {...state, count: state.count -1}
            break
        case "Reset":
            return {...state, count: 0}
            break
        default:
            return state
            break

    }

}


const Countreducer = () => {
    const [data, dispatch] = useReducer(reducerMeth, initial);

  return (
    <>
    <div>Counts:   {data.count}</div>
    <ButtonsContainer>
        <Button text="+" onClick={()=>dispatch({type:"Increment"})} />
        <Button  text="-" onClick={()=>dispatch({type:"Decrement"})} />
        <Button  text="Reset"  onClick={()=>dispatch({type:"Reset"})} />
    </ButtonsContainer>
    </>
  )
}

export default Countreducer
