import React, {useReducer, useState} from 'react'
import { Button } from '../../components/button'
import styled from 'styled-components';
import tw from 'twin.macro';
const ButtonsContainer = styled.div`
  ${tw`
    flex
    flex-wrap
    mt-4
  `};
`;

const initialState = {
    countd: 2
}
function reducer(state, action){
    switch(action.type){
        case "INCREMENT":
            return {...state, countd: state.countd + action.value}
            break;
        case "DECREMENT":
            return {...state, countd: state.countd - action.value}
            break;
        case "RESET":
            return {...state, count: 0}
            break;
        default: 
        return state;

    }

}

const Countreducer = () => {
    const [countvalue, dispatch] = useReducer(reducer, initialState);
  return (
    <>
    <div>Counts:   {countvalue.countd}</div>
    <ButtonsContainer>
        <Button text="+" onClick={()=>dispatch({type:"INCREMENT", value:5})} />
        <Button  text="-"  onClick={()=>dispatch({type: "DECREMENT", value: 3})}/>
        <Button  text="Reset"  onClick={()=>dispatch({type:"RESET"})}/>
    </ButtonsContainer>
    </>
  )
}

export default Countreducer
