import React, {useReducer, useState} from 'react'
import { Button } from '../../components/button'
import styled from 'styled-components';
import tw from 'twin.macro';
const ButtonsContainer = styled.div`
  ${tw`
    flex
    flex-wrap
    mt-4
  `};
`;

const initialState = {
    countd: 2
}
function reducer(state, action){
    switch(action.key){
        case "INCREMENT":
            return {...state, countd: state.countd + 1}
            break;
        case "DECREMENT":
            return {...state, countd: state.countd - 1}
            break;
        default: 
        return state;

    }

}

const Countreducer = () => {
    const [count, setCount] = useState(1);
    const [countvalue, dispatch] = useReducer(reducer, initialState);
  return (
    <>
    <div>Counts:   {initialState.countd}</div>
    {/* <div>Counts:   {count}</div> */}
    <ButtonsContainer>
        <Button text="+" onClick={()=>setCount((p)=>p+1)} />
        <Button  text="-"  onClick={()=>setCount(p=>p-1)}/>
        <Button  text="Reset"  onClick={()=>setCount(0)}/>
    </ButtonsContainer>
    </>
  )
}

export default Countreducer
